import React, { useEffect } from "react";
import Aos from "aos";
import "aos/dist/aos.css";

import { Container, Row, Col } from "react-bootstrap";
function Contact() {

  useEffect(() => {
   Aos.init({duration: 2000});
  }, []);

  return (
      <Container data-aos="fade-down">
        <Container className="contact-content">
          <Row>
          <Col md={12} style={{ textAlign: 'left'}}>
           <h2 style={{ color: '#0B0C2C', fontWeight: 'bold', marginBottom: 30}}>Now, about your project...</h2>
           <p style={{fontSize: '14px', color: '#A2A3AF', fontWeight: 'bold'}}>We like being on a first-name basis, but it also helps us get in touch with you.</p>
          </Col>


          <Col md={9}>
            <div className="contact-form">
             <div className="form-group">
              <label htmlFor="first-name">What is your name?</label>
              <input type="text" name="first-name" id="first-name" placeholder="Jonh" />
             </div>
             <div className="form-group">
             <label htmlFor="first-name">What is your second name?</label>
              <input type="text" name="first-name" id="first-name" placeholder="Smith" />
             </div>
             <div className="form-group">
              <label htmlFor="email">Type your best E-mail address: </label>
              <input type="email" name="email" id="email" placeholder="jonhsmith@mail.xyz" />
             </div>
            </div>
            </Col>

            <Col md={3}>
             <div>
                   <textarea name="message" id="message" cols="30" rows="10" placeholder="Your message"></textarea>
               </div>
            </Col>
          </Row>
        </Container>
      </Container>
  );
}

export default Contact;
